package com.mpx90.training_app.models.training;import jakarta.persistence.*;import lombok.*;import java.time.LocalDateTime;@Entity@Getter@Setter@NoArgsConstructor@AllArgsConstructor@Builder@Table(name = "rounds")public class RoundEntity {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @ManyToOne    @JoinColumn(name = "routine_id", nullable = false)    private RoutineEntity routine;    @ManyToOne    @JoinColumn(name = "round_type_id")    private RoundTypeEntity roundType;    @Column(nullable = false, name = "round_position")    private Integer roundPosition;    private Integer rest;    @Column(name = "created_at", nullable = false, updatable = false)    private LocalDateTime createdAt = LocalDateTime.now();    @PrePersist    protected void onCreate() {        this.createdAt = LocalDateTime.now();    }}